-- ----------------------------
-- Table structure for comment
-- ----------------------------
DROP TABLE IF EXISTS `comment`;
CREATE TABLE `comment` (
  `cid` int(5) NOT NULL AUTO_INCREMENT COMMENT '评论id',
  `c_con` text COLLATE utf8_general_mysql500_ci COMMENT '评论内容',
  `mid` int(5) DEFAULT NULL COMMENT '电影id',
  `uid` int(5) DEFAULT NULL COMMENT '用户id',
  `comtime` datetime DEFAULT NULL COMMENT '评论时间',
  `score` float(5,1) DEFAULT NULL COMMENT '电影评分',
  `status` int(1) DEFAULT '1' COMMENT '状态',
  PRIMARY KEY (`cid`)
) ENGINE=InnoDB AUTO_INCREMENT=22 DEFAULT CHARSET=utf8 COLLATE=utf8_general_mysql500_ci;

-- ----------------------------
-- Records of comment
-- ----------------------------
INSERT INTO `comment` VALUES ('1', '挺好看的，没有不好的学生，只有不好的老师，真的非常有教育意义的一部电影，值得大家看，内容题材非常的好，很真实', '24', '2', '2018-11-02 16:56:30', '3.0', '1');
INSERT INTO `comment` VALUES ('2', '挺好看的，没有不好的学生，只有不好的老师，真的非常有教育意义的一部电影，值得大家看，内容题材非常的好，很真实', '24', '2', '2018-11-02 16:57:30', '3.0', '1');
INSERT INTO `comment` VALUES ('3', '迪士尼的电影从没让我失望过 午休时间出来看包场电影 真的是可以把自己置身于童话世界中 去享受那一刻的美好 回归于现实生活中 我们每个人都是自己的主人公 在复杂的社会环境里 我们常常想要逃避 活在自己的小世界里 可是既然存活于社会之中 就没办法真正意义上的逃离 于是我们必须勇敢必须坚强 相信自己能够披荆斩棘 度过一重又一重的“难关” 偶尔我们甚至还要感谢这些“难关” 因为是它们让我们成长 让我们变得不再懦弱和胆小 让我们更加地勇敢和坚强 去面对未来的一切可能 愿我们迷茫过后都能找到自己的位置', '24', '21', '2018-11-02 17:03:48', '5.0', '1');
INSERT INTO `comment` VALUES ('4', '不愧是迪士尼，烂大街的故事也能拍出花来。', '24', '1', '2018-11-02 17:05:31', '4.0', '1');
INSERT INTO `comment` VALUES ('5', '我曾经是一名公办职业高中老师，这电影让我很感慨。这故事是我梦寐以求的故事发展。讽刺的是电影中任一问题出现在所带班级中，学校中层干部就带着处分来和你聊天了，根本不可能见到校长甚至提出自己的意见；老师们怀着得过且过的心态度日；行政管理部门秉持着不求犯错的宗旨办事；绝大多数学生被少数不良学生影响着。我怀着一腔热血来教书却一次次被泼冷水，想改变学校现状，不好意思你算什么东西；想改变班级，不好意思你没有任何的部门的支持；想教好学的学生，不好意思坏学生总能以各种途径搞好学生。我知道在很多人心中职高就是垃圾集中营，但我入职后却发现并不是的。学生们渴望着改变，但是对不起，我没有解决当今现状的方法并被现实打败了', '24', '1', '2018-11-02 17:07:07', '5.0', '1');
INSERT INTO `comment` VALUES ('6', '超级感动，国内为什么就不能静下心来好好拍一部电影，这几年印度高分电影真是大爆发，摔跤吧爸爸，神秘巨星，苏丹，加上这部嗝嗝老师，人家也没有大明星，也没有大流量小鲜肉，也没有几亿几亿的大制作经费。为什么人家能拍出这么好的电影，国内那些只知道请小鲜肉挣快钱的电影人你们不觉得羞愧吗？我是个普通人都这么想，可想而知外国那些专业的影评人怎么看待我们国内的。真心希望国内也能出这么好的片子，到时候我就可以为国产电影打气加油了。', '24', '1', '2018-11-02 17:10:29', '5.0', '1');
INSERT INTO `comment` VALUES ('7', '一直沉醉在水墨中国风种，色彩大师张艺谋这次很不一样，从演员的服装，到佩饰的雕琢，从背景的布置到细节的处理全都是实打实的水墨色彩，内蕴十足，张艺谋不愧是张艺谋。很久没看到如此中国风的电影登上大银幕了', '5', '2', '2018-11-02 17:27:28', '5.0', '1');
INSERT INTO `comment` VALUES ('8', '曹华胜', '24', '2', '2018-11-02 20:31:25', '5.0', '1');
INSERT INTO `comment` VALUES ('9', '可以，非常可以', '3', '3', '2018-11-02 21:04:52', '5.0', '1');
