-- ----------------------------
-- Table structure for theater
-- ----------------------------
DROP TABLE IF EXISTS `theater`;
CREATE TABLE `theater` (
  `theater_id` int(11) NOT NULL COMMENT '影院id',
  `theater_name` varchar(255) CHARACTER SET utf8 DEFAULT NULL COMMENT '影院名称',
  `cate` varchar(255) COLLATE utf8mb4_unicode_ci DEFAULT NULL COMMENT '品牌',
  `area` varchar(255) COLLATE utf8mb4_unicode_ci DEFAULT NULL COMMENT '所属行政区',
  `theater_adress` varchar(255) COLLATE utf8mb4_unicode_ci DEFAULT NULL COMMENT '影院地址'
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_unicode_ci;

