-- ----------------------------
-- Table structure for time
-- ----------------------------
DROP TABLE IF EXISTS `time`;
CREATE TABLE `time` (
  `time_id` int(11) NOT NULL COMMENT '场次时刻id',
  `time` varchar(255) CHARACTER SET utf8 DEFAULT NULL COMMENT '场次时刻',
  `sold_seats` varchar(255) COLLATE utf8mb4_unicode_ci DEFAULT NULL COMMENT '已售出座位id',
  `field_id` int(11) DEFAULT NULL COMMENT '影厅id',
  `mid` int(11) DEFAULT NULL COMMENT '电影id',
  `theater_id` int(11) DEFAULT NULL COMMENT '影院id',
  PRIMARY KEY (`time_id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_unicode_ci;

