-- ----------------------------
-- Table structure for user
-- ----------------------------
DROP TABLE IF EXISTS `user`;
CREATE TABLE `user` (
  `uid` int(11) NOT NULL AUTO_INCREMENT,
  `username` varchar(255) COLLATE utf8_general_mysql500_ci DEFAULT NULL,
  `sex` varchar(8) COLLATE utf8_general_mysql500_ci DEFAULT NULL,
  `headimg` varchar(255) COLLATE utf8_general_mysql500_ci DEFAULT NULL COMMENT '头像',
  `password` varchar(255) COLLATE utf8_general_mysql500_ci NOT NULL COMMENT '密码',
  `birthday` datetime DEFAULT NULL COMMENT '生日',
  `status` int(2) DEFAULT NULL COMMENT '状态',
  `interest` tinytext COLLATE utf8_general_mysql500_ci,
  `moto` varchar(50) COLLATE utf8_general_mysql500_ci DEFAULT NULL,
  PRIMARY KEY (`uid`)
) ENGINE=InnoDB AUTO_INCREMENT=3 DEFAULT CHARSET=utf8 COLLATE=utf8_general_mysql500_ci;

